for t in data:
    tese = Tese()

    if 'id' in t:
        tese.numero = t['id']
    tese.titulo = t['title']
    tese.resumo = t['description']

    tese.alunos =  ', '.join([ t[k]['name'].title() for k in t if 'student' in k])

    if t['schoolYear'] != '2022':
        continue
    tese.ano = Ano.objects.get(ano=2023)
    tese.ciclo = Ciclo.objects.get(numero=1)
    tese.link = t['reportDownloadUrl']
    tese.save()

    tese.cursos.add(Curso.objects.get(nome="Licenciatura em Engenharia Informática"))

    for tech in t['technologies']:
        t_object,criado = Tecnologia.objects.get_or_create(nome=tech)
        tese.tecnologias.add(t_object)

    for o in Orientador.objects.all():
        if t['supervisor']['name'].split()[-1] in o.nome:
            tese.orientadores.add(o)
            break

    tese.curso = 'LEI'
    tese.save()